mysql -u root -p tohme -e "select subbins.LabelId, subbins.LabelBinId, subbins.LabelGSVPanoramaId, subbins.LabelTypeId, LabelPoints.svImageX, LabelPoints.svImageY, LabelPoints.originalCanvasX, LabelPoints.originalCanvasY, LabelPoints.originalHeading, LabelPoints.heading, LabelPoints.originalPitch, LabelPoints.pitch, LabelPoints.labelLat, LabelPoints.labelLng from LabelPoints inner join (select Labels.LabelId, BinnedLabels.LabelBinId, Labels.LabelGSVPanoramaId, Labels.LabelTypeId from BinnedLabels inner join Labels on Labels.LabelId = BinnedLabels.LabelId) subbins on subbins.LabelId = LabelPoints.LabelId;" -B > tohme_binned.csv
