mysql -u root -p tohme -e "select subpoints.LabelId, subpoints.LabelGSVPanoramaId, subpoints.LabelTypeId, LabelPoints.svImageX, LabelPoints.svImageY, LabelPoints.originalCanvasX, LabelPoints.originalCanvasY, LabelPoints.originalHeading, LabelPoints.heading, LabelPoints.originalPitch, LabelPoints.pitch, LabelPoints.labelLat, LabelPoints.labelLng from LabelPoints inner join (select Assignments.AmazonTurkerId, subtasks.LabelId, subtasks.LabelTypeId, subtasks.LabelGSVPanoramaId from Assignments inner join (select Labels.LabelId, Labels.LabelingTaskId, Labels.LabelTypeId, Labels.LabelGSVPanoramaId, LabelingTasks.AssignmentId from Labels inner join LabelingTasks on Labels.LabelingTaskId = LabelingTasks.LabelingTaskId) subtasks on subtasks.AssignmentId = Assignments.AssignmentId where AmazonTurkerId = 'Researcher_Kotaro') subpoints on subpoints.LabelId = LabelPoints.LabelId;" -B > tohme_researcher_kotaro.csv
